var ProductList={dataUrl:"js/ProductList.js",container:".productGridContainer",speed:100,init:function(){var e=$.getJSON(this.dataUrl,function(e){}).done(function(e){$.each(e,function(e,t){ProductList.createTile(t,e)})}).fail(function(e,t,n){})},createTile:function(e,t){for(var n=0;n<e.length;n++){var r='<div data-cid="'+e[n].prodid+'" class="tile white tileProduct '+t+'">';r+='<div class="tileImg"><img src="'+e[n].img+'"></div>';r+='<div class="tileContent"><span>'+e[n].name+"</span></div>";r+='<a class="tileLink" href="#"></a></div>';$(this.container).append(r)}},sort:function(e){$(this.container).children().each(function(t){$(this).hasClass(e)?$(this).is(":visible")||$(this).fadeIn(this.speed):$(this).fadeOut(this.speed-90)})},sortCat:function(e){$(this.container).children().each(function(t){$(this).data("cid")==e?$(this).fadeIn(this.speed):$(this).fadeOut(this.speed-90)})},reset:function(){$(this.container).children().each(function(e){$(this).is(":hidden")&&$(this).fadeIn(this.speed)})}};