var Recruit={container:"jobs",init:function(e){var t=document.getElementById(this.container),n='<table width="100%" cellspacing="0" cellpadding="0" border="0">';n+="<thead><tr><td>Position title</td>";n+="<td>Department</td>";n+="<td>Location</td>";n+="<td>Closing date</td></tr></thead>";for(var r=0;r<e.Positions.length;r++){n+="<tr>";n+='<td><a href="'+e.Positions[r].Url+'">'+e.Positions[r].Title+"</a></td>";n+="<td>"+e.Positions[r].Department+"</td>";n+="<td>"+e.Positions[r].Location+"</td>";n+="<td>"+e.Positions[r].ClosingDate+"</td>";n+="</tr>"}n+="</table>";t.innerHTML=n}};